<?php//using namespace$applicants = \Initial\Controller\Application::get_all_applicant();?><div class="container-fluid mrt50">  <div class="panel panel-primary">    <div class="clearfix panel-custom-color">      <div class="panel-heading pull-left">Applicant List</div>    </div>    <div class="panel-body">      <p>form shortcode [show_submission_form]</p>      <?php include('message.php');?>      <?php if (isset($applicants) && count($applicants) > 0): ?>      <table id="form-submission" class="table table-striped table-hover table-bordered" style="width:100%">        <thead>        <tr>          <th width="10"><label class="control control-checkbox">              <input type="checkbox" id="selectall" class="checked"/>              <span class="control-indicator"></span>            </label></th>          <th>#</th>          <th>NAME</th>          <th>APPLIED FOR</th>          <th>DOB</th>          <th>GENDER</th>          <th>EMAIL</th>          <th>PHONE</th>          <th>NATIONALITY</th>          <th>CV</th>          <th>DATE</th>          <th>ACTION</th>        </tr>        </thead>        <tbody>        <?php $i=1; foreach ($applicants as $applicant): ?>        <tr id="<?php echo $applicant['id'] ; ?>">          <td><label class="control control-checkbox">              <input type="checkbox" onClick="checkbox_is_checked()" name="delete_applicant[]" value="<?php echo $applicant['id'] ; ?>" id='del_<?php echo $applicant['id']; ?>' data-app-id="<?php echo $applicant['id'] ; ?>" class="check-all app_checkbox">              <span class="control-indicator"></span>            </label></td>          <td><?php echo $i++ ; ?></td>          <td><?php echo $applicant['name'] ; ?></td>          <td><?php echo $applicant['post_applied'] ; ?></td>          <td><?php echo \Initial\Controller\Application::year_month_day($applicant['dob']).' ('.$applicant['dob'].')' ; ?></td>          <td><?php echo $applicant['gender'] ; ?></td>          <td><?php echo $applicant['email'] ; ?></td>          <td><?php echo $applicant['contactno'] ; ?></td>          <td><?php echo \Initial\Controller\Application::get_nationality_name_by_code($applicant['nationality']) ; ?></td>          <?php          $uploads_sub_dir = site_url() .'/wp-content/uploads/resume/'.$applicant['attachresume']; ?>          <td>          <?php if (file_exists($uploads_sub_dir)): ?>            <a href="<?php echo $uploads_sub_dir; ?>" class="app-btn app-btn-primary" download> Download CV </a>          <?php else: ?>            <a href="JavaScript:void(0);" class="app-btn app-btn-danger no_file cv-button"> NO CV </a>          <?php endif; ?>          </td>          <td><?php echo $applicant['created_at'] ; ?></td>          <td>            <a href="admin.php?page=applicant_info&view-id=<?php echo $applicant['id']; ?>" class="app-btn app-btn-primary" data-id="<?php echo $applicant['id'] ; ?>">View</a>            <a class="app-btn app-btn-danger removeApplication" href="javascript:void(0)" data-id="<?php echo $applicant['id'] ; ?>">Delete</a>          </td>        </tr>          <?php endforeach ?>        </tbody>      </table>      <?php else: ?>        <h3 class="text-center">No New Application</h3>      <?php endif ?>    </div>  </div></div>